'use client'

import React, { useState, useCallback } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { cn } from '@/lib/utils'

type ButtonVariant = 'default' | 'gradient' | 'outline' | 'ghost' | 'neon' | 'glass'
type ButtonSize = 'sm' | 'md' | 'lg'

interface AnimatedButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
    variant?: ButtonVariant
    size?: ButtonSize
    children: React.ReactNode
}

const AnimatedButton = React.forwardRef<HTMLButtonElement, AnimatedButtonProps>(
    ({ className, variant = 'default', size = 'md', children, onClick, ...props }, ref) => {

        // Ripple Effect State
        const [ripples, setRipples] = useState<{ x: number; y: number; id: number }[]>([])

        const handleClick = useCallback((e: React.MouseEvent<HTMLButtonElement>) => {
            const rect = e.currentTarget.getBoundingClientRect()
            const x = e.clientX - rect.left
            const y = e.clientY - rect.top
            const id = Date.now()
            setRipples((prev) => [...prev, { x, y, id }])
            setTimeout(() => setRipples((prev) => prev.filter((r) => r.id !== id)), 1000)
            onClick?.(e)
        }, [onClick])

        // Base Styles
        const baseStyles = "relative inline-flex items-center justify-center font-medium transition-all duration-300 ease-out rounded-lg overflow-hidden focus:outline-none focus:ring-2 focus:ring-offset-2 disabled:opacity-50 disabled:pointer-events-none"

        // Size Styles
        const sizeStyles = {
            sm: "px-4 py-2 text-sm",
            md: "px-6 py-3 text-base",
            lg: "px-8 py-4 text-lg",
        }

        // Variant Styles (Explicit & Robust)
        const variantStyles = {
            default: "bg-black hover:bg-neutral-800 text-white dark:bg-white dark:text-black dark:hover:bg-neutral-200 border border-transparent",
            gradient: "bg-gradient-to-r from-blue-600 via-purple-600 to-blue-600 bg-[length:200%_auto] text-white hover:brightness-110 shadow-lg shadow-purple-500/20",
            outline: "border-2 border-neutral-900 text-neutral-900 bg-transparent hover:bg-neutral-900 hover:text-white dark:border-white dark:text-white dark:hover:bg-white dark:hover:text-black",
            ghost: "bg-transparent text-neutral-700 hover:bg-neutral-100 dark:text-neutral-300 dark:hover:bg-neutral-800",
            neon: "bg-black text-cyan-400 border border-cyan-400 shadow-[0_0_10px_rgba(34,211,238,0.3)] hover:shadow-[0_0_20px_rgba(34,211,238,0.6)] hover:bg-cyan-950/30",
            glass: "bg-white/10 backdrop-blur-md border border-white/20 text-neutral-900 dark:text-white hover:bg-white/20 shadow-sm",
        }

        return (
            <motion.button
                ref={ref}
                className={cn(baseStyles, sizeStyles[size], variantStyles[variant], className)}
                onClick={handleClick}
                whileTap={{ scale: 0.97 }}
                whileHover={variant === 'gradient' ? { scale: 1.02 } : {}}
                animate={variant === 'gradient' ? { backgroundPosition: ["0% 50%", "100% 50%", "0% 50%"] } : {}}
                transition={variant === 'gradient' ? { duration: 3, repeat: Infinity, ease: "linear" } : {}}
                {...props}
            >
                {/* Micro-interaction: Shine for gradient */}
                {variant === 'gradient' && (
                    <motion.div
                        className="absolute inset-0 bg-white/20"
                        initial={{ x: "-100%" }}
                        animate={{ x: "100%" }}
                        transition={{ duration: 1.5, repeat: Infinity, repeatDelay: 1, ease: "linear" }}
                        style={{ skewX: -20 }}
                    />
                )}

                {/* Ripple Container */}
                <div className="absolute inset-0 overflow-hidden pointer-events-none">
                    <AnimatePresence>
                        {ripples.map((ripple) => (
                            <motion.span
                                key={ripple.id}
                                initial={{ opacity: 0.5, transform: "scale(0)" }}
                                animate={{ opacity: 0, transform: "scale(2.5)" }}
                                exit={{ opacity: 0 }}
                                transition={{ duration: 0.6 }}
                                className="absolute rounded-full bg-white/30"
                                style={{
                                    left: ripple.x,
                                    top: ripple.y,
                                    width: 100,
                                    height: 100,
                                    marginLeft: -50,
                                    marginTop: -50,
                                }}
                            />
                        ))}
                    </AnimatePresence>
                </div>

                {/* Content */}
                <span className="relative z-10 flex items-center gap-2">{children}</span>
            </motion.button>
        )
    }
)

AnimatedButton.displayName = "AnimatedButton"

export default AnimatedButton
